mapper.select:
select * from community 
where comm_num in (select comm_num from comm_user_list where user_num=#{user_num} and cul_status=1) 

	and user_num != #{user_num}


	and user_num = #{user_num}

		select * from community 
		where comm_num in (
                    select comm_num 
                    from (
                          select count(cul_num) 
                          from comm_user_list 
                          order by count(user_num) desc
                        )
                    )
