mapper.select:
select * from ( select aclist.*,rownum rnum from ( select ac.*, user1.user_nickname user1_nickname, user2.user_nickname user2_nickname from accuse ac left join members user1 on ac.user1_num=user1.user_num left join members user2 on ac.user2_num=user2.user_num
where ac.${field} like '%'||#{keyword}||'%'
order by ac_num desc )aclist ) where rnum>=#{startrow} and rnum<=#{endrow}
